(function(){tinymce.PluginManager.requireLangPack('upimage');tinymce.create('tinymce.plugins.UpImagePlugin',{init:function(ed,url){ed.addCommand('mceExample',function(){ed.windowManager.open({file:url+'/dialog.htm',width:320+parseInt(ed.getLang('upimage.delta_width',0)),height:420+parseInt(ed.getLang('upimage.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg',})});ed.addButton('upimage',{title:'upimage.desc',cmd:'mceExample',image:url+'/img/example.gif'})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Example plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"1.0"}}});tinymce.PluginManager.add('upimage',tinymce.plugins.UpImagePlugin)})();